// Bool returns a single bool from a group-by query.
// It is only allowed when executing a group-by query with one field.
func (egb *EvseGroupBy) Bool(ctx context.Context) (_ bool, err error) {
	var v []bool
	if v, err = egb.Bools(ctx); err != nil {
		return
	}
	switch len(v) {
	case 1:
		return v[0], nil
	case 0:
		err = &NotFoundError{evse.Label}
	default:
		err = fmt.Errorf("ent: EvseGroupBy.Bools returned %d results when one was expected", len(v))
	}
	return
}

